mapper.update:
UPDATE student_class
SET attendance = (case

        WHEN (student_id = #{item.studentId}) THEN #{item.attendance}
     END)
WHERE class_id = #{class_id} AND student_id IN

        #{item.studentId}

mapper.select:
SELECT *
FROM semester_class
WHERE class_id = #{class_id} AND week_no = #{week_no}
